PRINT N'Populate RefApipInteractionType table'
GO
IF EXISTS(SELECT * FROM dbo.sysobjects WHERE id = object_id(N'[dbo].[RefApipInteractionType]') and OBJECTPROPERTY(id, N'IsUserTable') = 1)
BEGIN
INSERT INTO dbo.[RefApipInteractionType]([Code], [Description], [Definition], [SortOrder])
VALUES ('CustomInteraction', 'Custom Interaction', 'The custom interaction provides an opportunity for extensibility of this specification to include support for interactions not currently documented.', 1.00)
INSERT INTO dbo.[RefApipInteractionType]([Code], [Description], [Definition], [SortOrder])
VALUES ('DrawingInteraction', 'Drawing Interaction', 'The drawing interaction allows the candidate to use a common set of drawing tools to modify a given graphical image (the canvas). It must be bound to a response variable with base-type file and single cardinality. The result is a file in the same format as the original image.', 2.00)
INSERT INTO dbo.[RefApipInteractionType]([Code], [Description], [Definition], [SortOrder])
VALUES ('GapMatchInteraction', 'Gap Match Interaction', 'A gap match interaction contains a number gaps that the candidate can fill from an associated set of choices. The candidate must be able to review the content with the gaps filled in context, as indicated by their choices.', 3.00)
INSERT INTO dbo.[RefApipInteractionType]([Code], [Description], [Definition], [SortOrder])
VALUES ('MatchInteraction', 'Match Interaction', 'A match interaction presents candidates with two sets of choices and allows them to create associates between pairs of choices in the two sets, but not between pairs of choices in the same set. Further restrictions can still be placed on the allowable associations using the matchMax attribute of the choices.', 4.00)
INSERT INTO dbo.[RefApipInteractionType]([Code], [Description], [Definition], [SortOrder])
VALUES ('GraphicGapMatchInteraction', 'Graphic Gap Match Interaction', 'A graphic gap-match interaction is a graphical interaction with a set of gaps that are defined as areas (hotspots) of the graphic image and an additional set of gap choices that are defined outside the image. The candidate must associate the gap choices with the gaps in the image and be able to review the image with the gaps filled in context, as indicated by their choices. Care should be taken when designing these interactions to ensure that the gaps in the image are a suitable size to receive the required gap choices. It must be clear to the candidate which hotspot each choice has been associated with. When associated, choices must appear wholly inside the gaps if at all possible and, where overlaps are required, should not hide each other completely. If the candidate indicates the association by positioning the choice over the gap (e.g., drag and drop) the system should ''snap'' it to the nearest position that satisfies these requirements.', 5.00)
INSERT INTO dbo.[RefApipInteractionType]([Code], [Description], [Definition], [SortOrder])
VALUES ('HotspotInteraction', 'Hotspot Interaction', 'A hotspot interaction is a graphical interaction with a corresponding set of choices that are defined as areas of the graphic image. The candidate''s task is to select one or more of the areas (hotspots). The hotspot interaction should only be used when the spatial relationship of the choices with respect to each other (as represented by the graphic image) is important to the needs of the item. Otherwise, choiceInteraction should be used instead with separate material for each option.', 6.00)
INSERT INTO dbo.[RefApipInteractionType]([Code], [Description], [Definition], [SortOrder])
VALUES ('GraphicOrderInteraction', 'Graphic Order Interaction', 'A graphic order interaction is a graphic interaction with a corresponding set of choices that are defined as areas of the graphic image. The candidate''s task is to impose an ordering on the areas (hotspots). The order hotspot interaction should only be used when the spatial relationship of the choices with respect to each other (as represented by the graphic image) is important to the needs of the item. Otherwise, orderInteraction should be used instead with separate material for each option.', 7.00)
INSERT INTO dbo.[RefApipInteractionType]([Code], [Description], [Definition], [SortOrder])
VALUES ('GraphicAssociateInteraction', 'Graphic Associate Interaction', 'A graphic associate interaction is a graphic interaction with a corresponding set of choices that are defined as areas of the graphic image. The candidate''s task is to associate the areas (hotspots) with each other. The graphic associate interaction should only be used when the graphical relationship of the choices with respect to each other (as represented by the graphic image) is important to the needs of the item. Otherwise, associateInteraction should be used instead with separate Material for each option.
SliderInteraction -- The slider interaction presents the candidate with a control for selecting a numerical value between a lower and upper bound. It must be bound to a response variable with single cardinality with a base-type of either integer or float.', 8.00)
INSERT INTO dbo.[RefApipInteractionType]([Code], [Description], [Definition], [SortOrder])
VALUES ('ChoiceInteraction', 'Choice Interaction', 'The choice interaction presents a set of choices to the candidate. The candidate''s task is to select one or more of the choices, up to a maximum of maxChoices. The interaction is always initialized with no choices selected.', 9.00)
INSERT INTO dbo.[RefApipInteractionType]([Code], [Description], [Definition], [SortOrder])
VALUES ('InlineChoiceInteraction', 'Inline Choice Interaction', 'A inline choice is an inlineInteraction that presents the user with a set of choices, each of which is a simple piece of text. The candidate''s task is to select one of the choices. Unlike the choiceInteraction, the delivery engine must allow the candidate to review their choice within the context of the surrounding text.', 10.00)
INSERT INTO dbo.[RefApipInteractionType]([Code], [Description], [Definition], [SortOrder])
VALUES ('MediaInteraction', 'Media Interaction', 'The media interaction allows more control over the way the candidate interacts with a time-based media object and allows the number of times the media object was experienced to be reported in the value of the associated response variable, which must be of base-type integer and single cardinality.', 11.00)
INSERT INTO dbo.[RefApipInteractionType]([Code], [Description], [Definition], [SortOrder])
VALUES ('HottextInteraction', 'Hottext Interaction', 'The hottext interaction presents a set of choices to the candidate represented as selectable runs of text embedded within a surrounding context, such as a simple passage of text. Like choiceInteraction, the candidate''s task is to select one or more of the choices, up to a maximum of maxChoices. The interaction is initialized from the defaultValue of the associated response variable, a NULL value indicating that no choices are selected (the usual case).', 12.00)
INSERT INTO dbo.[RefApipInteractionType]([Code], [Description], [Definition], [SortOrder])
VALUES ('OrderInteraction', 'Order Interaction', 'In an order interaction the candidate''s task is to reorder the choices, the order in which the choices are displayed initially is significant. By default the candidate''s task is to order all of the choices but a subset of the choices can be requested using the maxChoices and minChoices attributes. When specified the candidate must select a subset of the choices and impose an ordering on them.', 13.00)
INSERT INTO dbo.[RefApipInteractionType]([Code], [Description], [Definition], [SortOrder])
VALUES ('PositionObjectInteraction', 'Position Object Interaction', 'The position object interaction consists of a single image which must be positioned on another graphic image (the stage) by the candidate. Like selectPointInteraction, the associated response may have an areaMapping that scores the response on the basis of comparing it against predefined areas but the delivery engine must not indicate these areas of the stage. Only the actual position(s) selected by the candidate shall be indicated.', 14.00)
INSERT INTO dbo.[RefApipInteractionType]([Code], [Description], [Definition], [SortOrder])
VALUES ('TextEntryInteraction', 'Text Entry Interaction', 'A textEntry interaction obtains a simple piece of text from the candidate. Like inlineChoiceInteraction, the delivery engine must allow the candidate to review their choice within the context of the surrounding text.', 15.00)
INSERT INTO dbo.[RefApipInteractionType]([Code], [Description], [Definition], [SortOrder])
VALUES ('ExtendedTextInteraction', 'Extended Text Interaction', 'An extended text interaction allows the candidate to enter an extended amount of text.', 16.00)
INSERT INTO dbo.[RefApipInteractionType]([Code], [Description], [Definition], [SortOrder])
VALUES ('EndAttemptInteraction', 'End Attempt Interaction', 'The end attempt interaction is a special type of interaction which allows item authors to provide the candidate with control over the way in which the candidate terminates an attempt. The candidate can use the interaction to terminate the attempt (triggering response processing) immediately, typically to request a hint. It must be bound to a response variable with base-type boolean and single cardinality.', 17.00)
INSERT INTO dbo.[RefApipInteractionType]([Code], [Description], [Definition], [SortOrder])
VALUES ('UploadInteraction', 'Upload Interaction', 'The upload interaction allows the candidate to upload a pre-prepared file representing their response. It must be bound to a response variable with base-type file and single cardinality.', 18.00)
INSERT INTO dbo.[RefApipInteractionType]([Code], [Description], [Definition], [SortOrder])
VALUES ('AssociateInteraction', 'Associate Interaction', 'An associate interaction presents candidates with a number of choices and allows them to create associations between them.', 19.00)
END
