INSERT INTO dbo.[RefFacilityMaintStandardType]([Code], [Description], [Definition], [SortOrder])
VALUES ('02837', 'Routine', 'A work-order based approach to maintenance that addresses lists of broken items, deteriorated finishes or equipment/parts nearing the end of their life cycle, maintenance required as a result of normal wear and tear, equipment/parts that should be fixed or replaced during a regular work day eliminating the need for overtime pay.', 4.00)
INSERT INTO dbo.[RefFacilityMaintStandardType]([Code], [Description], [Definition], [SortOrder])
