IF EXISTS(SELECT * FROM dbo.sysobjects WHERE id = object_id(N'[dbo].[RefFacilityMortgageInterestType]') and OBJECTPROPERTY(id, N'IsUserTable') = 1)
BEGIN
INSERT INTO dbo.[RefFacilityMortgageInterestType]([Code], [Description], [Definition], [SortOrder])
VALUES ('13730', 'Add-on interest', 'Interest added to the amount of the loan on the front end, or beginning of the loan repayment period. The balance (of principal and interest) is then paid by installments. This form of interest is much more expensive than simple interest paid on the entire amount for the entire term of the loan.', 1.00)
INSERT INTO dbo.[RefFacilityMortgageInterestType]([Code], [Description], [Definition], [SortOrder])
VALUES ('13731', 'Adjustable-Rate Mortgage (ARM)', 'A mortgage in which interest and payment rates vary periodically, based on a specific index, such as 30-year Treasury bills or the Cost-of-Funds index.', 2.00)
INSERT INTO dbo.[RefFacilityMortgageInterestType]([Code], [Description], [Definition], [SortOrder])
VALUES ('13732', 'Balloon Mortgage', 'A short-term mortgage, generally at a fixed rate of interest, to be paid back in pre-determined, equal monthly payments, with a large, final payment for the balance of the loan paid at the end of the term.', 3.00)
INSERT INTO dbo.[RefFacilityMortgageInterestType]([Code], [Description], [Definition], [SortOrder])
VALUES ('13733', 'Deferred Interest', 'Interest due but unpaid. Mortgages that permit negative amortization (such as a graduated-payment mortgage or a floating rate loan without a rate cap) will allow deferred interest.', 4.00)
INSERT INTO dbo.[RefFacilityMortgageInterestType]([Code], [Description], [Definition], [SortOrder])
VALUES ('13734', 'Fixed Payment Mortgage', 'Periodic payments of principal and interest on a mortgage which remain constant over the loan term.', 5.00)
INSERT INTO dbo.[RefFacilityMortgageInterestType]([Code], [Description], [Definition], [SortOrder])
VALUES ('13735', 'Fixed-rate Mortgage', 'A mortgage in which the interest rate does not change during the entire term of the loan.', 6.00)
INSERT INTO dbo.[RefFacilityMortgageInterestType]([Code], [Description], [Definition], [SortOrder])
VALUES ('13736', 'Floating Rate', 'An interest rate on a loan which changes during the life of the loan, often monthly, but can change quarterly or annually. These variable rates are based on an index rate and can be used for mortgages and other loans such as lines of credit.', 7.00)
INSERT INTO dbo.[RefFacilityMortgageInterestType]([Code], [Description], [Definition], [SortOrder])
VALUES ('13737', 'Fully Amortizing Mortgage', 'A method of loan amortization in which equal periodic payments completely repay the loan during the loan term.', 8.00)
INSERT INTO dbo.[RefFacilityMortgageInterestType]([Code], [Description], [Definition], [SortOrder])
VALUES ('13738', 'Graduated-payment Mortgage (GPM)', 'A type of stepped-payment loan in which the borrower''s payments are initially lower than those on a comparable level-rate mortgage. The payments gradually increase over a predetermined period, and then cap at a final level for the duration of the mortgage.', 9.00)
INSERT INTO dbo.[RefFacilityMortgageInterestType]([Code], [Description], [Definition], [SortOrder])
VALUES ('13739', 'Interest-only loan', 'A method of loan amortization in which interest is paid periodically over the term of the loan and the entire loan amount (principal) is paid at maturity.', 10.00)
INSERT INTO dbo.[RefFacilityMortgageInterestType]([Code], [Description], [Definition], [SortOrder])
VALUES ('13740', 'Open-end Mortgage', 'A mortgage agreement that allows the mortgagor to borrow additional funds from the mortgage lender in the future, normally with a borrowing limit. This limit may be based on loan-to-value or may simply be the original loan amount.', 11.00)
