INSERT INTO dbo.[RefK12StaffClassification]([Code], [Description], [Definition], [SortOrder])
VALUES ('InstructionalCoordinators', 'Instructional Coordinators', 'Staff supervising instructional programs including  professional staff members or supervisors in an early learning program assigned specific duties related to instruction ,  or supervisors at a school district or sub-district level including curriculum coordinators or supervisors; in-service training staff; Title I supervisors; home economics supervisors; educational television staff; coordinators and supervisors of audiovisual services; and staff engaged in development of computer-assisted instruction.', 21.00)
INSERT INTO dbo.[RefK12StaffClassification]([Code], [Description], [Definition], [SortOrder])
