INSERT INTO dbo.[RefK12StaffClassification]([Code], [Description], [Definition], [SortOrder])
VALUES ('LibraryMediaSpecialists', 'Librarians/Media Specialists', 'A professional staff member or supervisor assigned specific duties and school time for professional library and media service activities. Activities include: selecting, acquiring, preparing, cataloging, and circulating books and other printed materials; planning the use of library and media services by students, teachers and other members of the instructional staff; and guiding individuals in their use of media services and library materials, (whether maintained separately or as part of an instructional materials center).', 25.00)
INSERT INTO dbo.[RefK12StaffClassification]([Code], [Description], [Definition], [SortOrder])
