INSERT INTO dbo.[RefK12StaffClassification]([Code], [Description], [Definition], [SortOrder])
VALUES ('Paraprofessionals', 'Paraprofessionals', 'Paraprofessionals are employees who provide instructional support, including those who: (1) provide one-on-one tutoring if such tutoring is scheduled at a time when a student would not otherwise receive instruction from a teacher, (2) assist with classroom management, such as organizing instructional and other materials, (3) provide instructional assistance in a computer laboratory, (4) conduct parental involvement activities, (5) provide support in a library or media center, (6) act as a translator, or (7) provide instructional support services under the direct supervision of a teacher.', 35.00)
INSERT INTO dbo.[RefK12StaffClassification]([Code], [Description], [Definition], [SortOrder])
VALUES ('PartCEarlyInterventionists', 'Part C Early Interventionists', 'Professional staff members employed to provide early intervention services to infants and toddlers with disabilities or at-risk of experiencing a substantial developmental delay as defined by Part C of the  Individuals with Disabilities Education Act (IDEA): The Early Intervention Program for Infants and Toddlers with Disabilities.', 37.00)
INSERT INTO dbo.[RefK12StaffClassification]([Code], [Description], [Definition], [SortOrder])
