INSERT INTO dbo.[RefK12StaffClassification]([Code], [Description], [Definition], [SortOrder])
VALUES ('SchoolCounselors', 'School Counselors', 'Professional staff members assigned specific duties and school time for any of the following activities: counseling with students and parents, consulting with other staff members on learning problems, evaluating student abilities, assisting students in making education and career choices, assisting students in personal and social development, providing referral assistance, and/or working with other staff members in planning and conducting guidance programs for students. School counselors may be reported by elementary and secondary level when that information is available.', 41.00)
INSERT INTO dbo.[RefK12StaffClassification]([Code], [Description], [Definition], [SortOrder])
