INSERT INTO dbo.[RefK12StaffClassification]([Code], [Description], [Definition], [SortOrder])
VALUES ('SpecialEducationTeachers', 'Special Education Teachers', 'Special Education teachers include teachers employed to provide special education services to children with disabilities, including preschool teachers, itinerant/consulting teachers, and home/hospital teachers.  This should include teachers of children with disabilities in separate schools and facilities.', 47.00)
INSERT INTO dbo.[RefK12StaffClassification]([Code], [Description], [Definition], [SortOrder])
VALUES ('SpecialNeedsSpecialists', 'Special Needs Specialists', 'Professional staff members or supervisors assigned specific duties related to special needs learners.', 49.00)
INSERT INTO dbo.[RefK12StaffClassification]([Code], [Description], [Definition], [SortOrder])
VALUES ('StudentSupportServicesStaff', 'Student Support Services Staff', 'Professional and supervisory staff providing non-instructional services to students including attendance officers; staff providing health, psychology, speech pathology, audiology, or social services; and supervisors of the preceding staff and of health, transportation, and food service workers.', 51.00)
