PRINT N'Populate RefStudentSupportServiceType table'
GO
IF EXISTS(SELECT * FROM dbo.sysobjects WHERE id = object_id(N'[dbo].[RefStudentSupportServiceType]') and OBJECTPROPERTY(id, N'IsUserTable') = 1)
BEGIN
INSERT INTO dbo.[RefStudentSupportServiceType]([Code], [Description], [Definition], [SortOrder])
VALUES ('00290', 'Adaptive physical education', 'Programs designed to foster the development of the following physical activities: physical and motor fitness: fundamental motor skills and patterns; skills in aquatics and dance, including individual and group games; and sports (including intramural and lifetime sports). This term also includes special physical education, adapted physical education, movement education, and motor development.', 4.00)
INSERT INTO dbo.[RefStudentSupportServiceType]([Code], [Description], [Definition], [SortOrder])
VALUES ('00291', 'Art therapy', 'Programs that enable individuals who have mental, emotional, social, or physical problems to achieve self-expression and emotional release by communicating their emotions and conflicts graphically through painting, drawing, sculpting or other art forms. Art therapy can be used as diagnostic tool or a treatment technique.', 8.00)
INSERT INTO dbo.[RefStudentSupportServiceType]([Code], [Description], [Definition], [SortOrder])
VALUES ('00292', 'Assistive technology services', 'Programs that directly assist an individual with disabilities in the selection, acquisition, or use of an assistive technology device. Such assistance includes: 1) the evaluation of the needs of an individual, including a functional evaluation of the individual in his or her customary environment; 2) purchasing, leasing, or otherwise providing for the acquisition of assistive technology devices; 3) selecting, designing, fitting, customizing, adopting, applying, maintaining, repairing, or replacing assistive technology devices; 4) coordinating and using other therapies, interventions, or services (e.g., those associated with existing education and rehabilitation plans and programs); 5) training and technical assistance for an individual or, if appropriate, an individuals'' family members; and 6) training or technical assistance for professionals (including those providing early intervention services), employers, or other individuals who are substantially involved.', 12.00)
INSERT INTO dbo.[RefStudentSupportServiceType]([Code], [Description], [Definition], [SortOrder])
VALUES ('00293', 'Audiological services', 'Services that include: 1)identification of individuals with hearing loss: 2) determination of the range, nature, and degree of hearing loss, and communication functions, by use of audiological evaluation, and speech conservation; 4) creation and administration of programs for prevention of hearing loss; 5) counseling and guidance of students, parent/guardians, and teachers regarding hearing loss; and 6) determination of an individual''s need for group and individual amplification, selecting and fitting an appropriate aid, and evaluating the effectiveness of amplification.', 16.00)
INSERT INTO dbo.[RefStudentSupportServiceType]([Code], [Description], [Definition], [SortOrder])
VALUES ('73050', 'Augmentative Communication Service', 'Augmentative Communication Service is provided to the person or a group of persons.', 17.00)
INSERT INTO dbo.[RefStudentSupportServiceType]([Code], [Description], [Definition], [SortOrder])
VALUES ('73051', 'Autism Spectrum Disorder Service', 'Autism Spectrum Disorder Service is provided to the person or a group of persons.', 18.00)
INSERT INTO dbo.[RefStudentSupportServiceType]([Code], [Description], [Definition], [SortOrder])
VALUES ('73052', 'Behavior and Behavioral Consultation Service', 'Behavior and Behavioral Consultation Service is provided to the person or a group of persons.', 19.00)
INSERT INTO dbo.[RefStudentSupportServiceType]([Code], [Description], [Definition], [SortOrder])
VALUES ('00878', 'Case management services', 'Programs that develop case plans for the evaluation, treatment and/or care of individuals who, because of age, illness, disability or other difficulties, are unable to arrange for services on their own behalf. The services also assess the individual''s needs, coordinate the delivery of services, ensure that services are obtained in accordance with the case plan, and monitor progress to ensure that services are having a desired impact.', 20.00)
INSERT INTO dbo.[RefStudentSupportServiceType]([Code], [Description], [Definition], [SortOrder])
VALUES ('73053', 'Braille Service', 'Braille Service is provided to the person or a group of persons.', 20.00)
INSERT INTO dbo.[RefStudentSupportServiceType]([Code], [Description], [Definition], [SortOrder])
VALUES ('00295', 'Children''s protective services', 'Programs that investigate reports of child abuse, neglect, and abandonment, and provide for the temporary placement of a child who, for his or her own protection, has been removed from custody of his or her parent/guardians. Program staff works with families that are experiencing the problem of child abuse with the objective of facilitating continued family unification or reunification. These programs also provide ongoing support services for children in permanent placement.', 24.00)
INSERT INTO dbo.[RefStudentSupportServiceType]([Code], [Description], [Definition], [SortOrder])
VALUES ('00881', 'Communication services', 'Programs that help participants develop or refine their communication skills, usually by focusing on communication in a particular context (e.g., helping a parent/guardian communicate with a child or helping professionals communicate with their clients). Training may emphasize active listening skills, problem-solving skills, conflict resolution techniques, body language, empathy, sensitivity to the values and/or cultural background of other, genuineness and other facets of the communication process.', 28.00)
INSERT INTO dbo.[RefStudentSupportServiceType]([Code], [Description], [Definition], [SortOrder])
VALUES ('00882', 'Community recreational services', 'Programs that provide equipment and facilities that ensure individuals with special needs can participate in specific recreational activities, games, or sports of their choice, regardless of age.', 32.00)
INSERT INTO dbo.[RefStudentSupportServiceType]([Code], [Description], [Definition], [SortOrder])
VALUES ('73048', 'Curriculum planning', 'Curriculum planning is provided to the person or a group of persons.', 33.00)
INSERT INTO dbo.[RefStudentSupportServiceType]([Code], [Description], [Definition], [SortOrder])
VALUES ('00334', 'Developmental childcare program', 'An organized program that provides childcare services on a regular basis for a group of children; the program is directed toward the facilitation of several developmental areas.', 36.00)
INSERT INTO dbo.[RefStudentSupportServiceType]([Code], [Description], [Definition], [SortOrder])
VALUES ('73047', 'Early Intervention / Early Childhood Special Education Special Service', 'The design of learning environments and activities that promote the child''s acquisition of skills in a variety of developmental areas, include cognitive processes and social interaction. ', 37.00)
INSERT INTO dbo.[RefStudentSupportServiceType]([Code], [Description], [Definition], [SortOrder])
VALUES ('00297', 'Early intervention services', 'A program which provides services that: 1) are designed to meet the development needs of each child and family eligible, and are focused on enhancing the child''s development; 2) are selected in collaboration with the parent/guardian; 3) are provided under public supervision by qualified personnel, in conformity with an individualized family service plan; 4) are offered at no cost unless subject to federal or state law mandating a system of payments (including a schedule of sliding fees); and 5) meet the standards of the state.', 40.00)
INSERT INTO dbo.[RefStudentSupportServiceType]([Code], [Description], [Definition], [SortOrder])
VALUES ('00298', 'Educational therapy', 'Programs that provide diagnostic and treatment services which combine psychotherapeutic and tutorial techniques for individuals who have learning disabilities such as dyslexia, perceptual problems, emotional problems or other difficulties.', 44.00)
INSERT INTO dbo.[RefStudentSupportServiceType]([Code], [Description], [Definition], [SortOrder])
VALUES ('73054', 'ESL/Migrant Service', 'ESL/Migrant Service is provided to the person or a group of persons.', 45.00)
INSERT INTO dbo.[RefStudentSupportServiceType]([Code], [Description], [Definition], [SortOrder])
VALUES ('00299', 'Family counseling', 'Programs that offer therapeutic sessions that focus on the system of relationships and communication patterns between family members and attempt to modify those relationships and patterns to achieve greater harmony. The therapist focuses on the family as a unit rather than concentrating on the member who has been singled out as being in need of treatment.', 48.00)
INSERT INTO dbo.[RefStudentSupportServiceType]([Code], [Description], [Definition], [SortOrder])
VALUES ('00333', 'Family training, counseling, and home visits', 'Services provided, as appropriate, by social workers, psychologists, and other qualified personnel to assist the family of a child eligible under this part in understanding the special needs of the child and enhancing the child''s development.', 52.00)
INSERT INTO dbo.[RefStudentSupportServiceType]([Code], [Description], [Definition], [SortOrder])
VALUES ('00303', 'Health care', 'Programs whose primary purpose is to help individuals achieve and maintain physical well-being through the study, prevention, screening, evaluation and treatment of individuals who have illnesses, injuries, or disabilities.', 56.00)
INSERT INTO dbo.[RefStudentSupportServiceType]([Code], [Description], [Definition], [SortOrder])
VALUES ('00883', 'Independent living', 'Assistance provided to individuals with special needs including those with disabilities: 1) to learn the basic skills of daily living through individual and group counseling and instruction; 2) to experience and practice coping with real or simulated life situational demands; or 3) to use assistive devices, special equipment, or specialized assistant.', 60.00)
INSERT INTO dbo.[RefStudentSupportServiceType]([Code], [Description], [Definition], [SortOrder])
VALUES ('73049', 'Instructional Aide/Assistant/Intervener Service', 'Instructional Aide/Assistant/Intervener Service is provided to the person or a group of persons.', 61.00)
INSERT INTO dbo.[RefStudentSupportServiceType]([Code], [Description], [Definition], [SortOrder])
VALUES ('00304', 'Interpretation for the hearing impaired', 'Services that enable students who have hearing impairments to communicate their needs manually, verbally, or in writing.', 64.00)
INSERT INTO dbo.[RefStudentSupportServiceType]([Code], [Description], [Definition], [SortOrder])
VALUES ('00332', 'Medical services only for diagnostic or evaluation purposes', 'Services provided by a licensed physician to determine a child''s developmental status and need for early intervention services.', 68.00)
INSERT INTO dbo.[RefStudentSupportServiceType]([Code], [Description], [Definition], [SortOrder])
VALUES ('00305', 'Mental health counseling', 'Programs that specialize in the treatment of students who have adjustment or behavior problems, emotional disturbances, personality disorders, incipient illnesses, and/or other comparable conditions', 72.00)
INSERT INTO dbo.[RefStudentSupportServiceType]([Code], [Description], [Definition], [SortOrder])
VALUES ('00884', 'Mental health services', 'Programs that provide services to individuals who have been hospitalized for treatment of acute or chronic mental or emotional disturbances, and facilitate their return to the community. The programs also offer psychiatric treatment in a setting that is less restrictive than inpatient hospitalization.', 76.00)
INSERT INTO dbo.[RefStudentSupportServiceType]([Code], [Description], [Definition], [SortOrder])
VALUES ('00306', 'Music therapy', 'Programs that use music and music-related activities to help the student maintain mental functioning, to facilitate social and emotional growth, to promote communication, to develop constructive use of leisure time and to improve or maintain motor and perceptual skills. Music can be used as a passive agent as in the case of listening to music to aid in reminiscence, reality orientation or relaxation; or as an active creative process in which the client participates in musical production.', 80.00)
INSERT INTO dbo.[RefStudentSupportServiceType]([Code], [Description], [Definition], [SortOrder])
VALUES ('00300', 'National School Nutrition programs', 'A federally-funded program that provides supplemental nutrition in the form of a free meal, free snack, or free milk for income-eligible students who are unable to pay the cost. Public and non-profit private schools participating in the program are reimbursed for the meal/snack/milk served.', 84.00)
INSERT INTO dbo.[RefStudentSupportServiceType]([Code], [Description], [Definition], [SortOrder])
VALUES ('00308', 'Note-taking assistance', 'Programs that assist students who are unable to make their own record of classes or lectures.', 88.00)
INSERT INTO dbo.[RefStudentSupportServiceType]([Code], [Description], [Definition], [SortOrder])
VALUES ('00335', 'Nursing service', 'Services that are provided for the assessment of health status for the purpose of providing nursing care, including the identification of patterns of human response to actual or potential health problems. They also include the provision of nursing care to prevent health problems, restore or improve functioning, and promote optimal health and development. Finally, they involve the administration of medications, treatments, and regimens prescribed by a licensed physician.', 92.00)
INSERT INTO dbo.[RefStudentSupportServiceType]([Code], [Description], [Definition], [SortOrder])
VALUES ('00336', 'Nutrition services', 'Services that conduct individual assessments in nutritional history and dietary intake; anthropometrics, biochemical, and clinical variables; feeding skills and feeding problems; and food habits and food preferences. They also develop and monitor appropriate plans to address the nutritional needs of eligible children, and make referrals to appropriate community resources to carry out nutritional programs.', 96.00)
INSERT INTO dbo.[RefStudentSupportServiceType]([Code], [Description], [Definition], [SortOrder])
VALUES ('00309', 'Occupational therapy', 'Services that address the functional needs of an individual relating to adaptive development, adaptive behavior and play, and sensory, motor, and postural development. These services are designed to improve the individual''s functional ability to perform tasks in the home, school, and community settings and include: 1) identification assessment and intervention; 2) adaptation of the environment, and selection, design and fabrication of assistive and orthodontic devices to facilitate the development and promote the acquisition of functional skills; and 3) prevention or minimization of the impact of initial or future impairment, delay in development, or loss of functional ability.', 100.00)
INSERT INTO dbo.[RefStudentSupportServiceType]([Code], [Description], [Definition], [SortOrder])
VALUES ('00310', 'Orientation and mobility services', 'Programs that help individuals who are blind or who have visual impairments to develop the fundamental spatial concepts and skills that are necessary for maximum mobility and independent living. Instruction focuses on moving safely and efficiently in the school, home or community environment, and usually includes procedures for street crossings, travel in unfamiliar areas, utilization of public transportation, and appropriate use of aids such as sighted guides or canes.', 104.00)
INSERT INTO dbo.[RefStudentSupportServiceType]([Code], [Description], [Definition], [SortOrder])
VALUES ('00311', 'Parenting skills assistance', 'Programs that offer classes, groups, or other educational opportunities for parents or potential parents who want to acquire the knowledge and skills to be effective in their parenting role.', 108.00)
INSERT INTO dbo.[RefStudentSupportServiceType]([Code], [Description], [Definition], [SortOrder])
VALUES ('00312', 'Peer services', 'Programs that specialize in assisting the student through peer interaction. Such programs include peer counseling, tutoring, and leadership activities.', 112.00)
INSERT INTO dbo.[RefStudentSupportServiceType]([Code], [Description], [Definition], [SortOrder])
VALUES ('00313', 'Physical therapy', 'Services to prevent or alleviate movement dysfunction and related functional problems that include: 1) screening, evaluation, and assessment of children and youth with disabilities to identify movement dysfunction; 2) obtaining, interpreting, and integrating information appropriate to program planning to prevent or alleviate movement dysfunction and related functional problems; and 3) providing individual or group services or treatment to prevent, alleviate, or compensate for movement dysfunction and related functional problems.', 116.00)
INSERT INTO dbo.[RefStudentSupportServiceType]([Code], [Description], [Definition], [SortOrder])
VALUES ('00331', 'Psychological services', 'Services that address the psychological needs of the individual through the administering of psychological needs of the individual through the administering of psychological and developmental tests, and other assessment procedures. They also involve the interpretation of assessment results. In addition, they include obtaining, integrating, and interpreting information about individual behavior, and individual and family conditions related to learning, mental health, and development. Finally, the services also involve the planning and management of a program of psychological services, including psychological counseling for children and parents, family counseling, consultation on child development, parent training, and education programs.', 120.00)
INSERT INTO dbo.[RefStudentSupportServiceType]([Code], [Description], [Definition], [SortOrder])
VALUES ('00314', 'Reader service', 'Programs that provide textbook reading services for individuals who are blind or have visual impairments.', 124.00)
INSERT INTO dbo.[RefStudentSupportServiceType]([Code], [Description], [Definition], [SortOrder])
VALUES ('00315', 'Recreation service', 'Programs that may include: 1) assessment of leisure function; 2) therapeutic recreation services; 3) recreation programs in schools and community agencies; and 4) leisure education.', 128.00)
INSERT INTO dbo.[RefStudentSupportServiceType]([Code], [Description], [Definition], [SortOrder])
VALUES ('00318', 'Rehabilitation counseling services', 'Programs that provide a combination of treatment and education services designed to restore maximum functioning , a sense of well-being, and a personally satisfying level of independence in individuals who have temporary or permanent disabilities.', 132.00)
INSERT INTO dbo.[RefStudentSupportServiceType]([Code], [Description], [Definition], [SortOrder])
VALUES ('00885', 'Residential services', 'Programs that provide a therapeutic living environment in community-based residential facilities for individuals with special needs, including those with emotional and/or behavioral problems. Such individuals require a structure, supervised treatment program that may include individual, group, family, and other treatment modalities as appropriate.', 136.00)
INSERT INTO dbo.[RefStudentSupportServiceType]([Code], [Description], [Definition], [SortOrder])
VALUES ('73046', 'Respite Care', 'Temporary childcare services that are short-term and non-medical in nature, provided either in or out of the home. These services are designed to provide temporary relief to the primary caregiver.', 137.00)
INSERT INTO dbo.[RefStudentSupportServiceType]([Code], [Description], [Definition], [SortOrder])
VALUES ('00319', 'School clothing', 'Programs that pay for or provide new or used clothing to students who need appropriate school attire and whose parent/guardians are unable to afford these items at retail prices.', 140.00)
INSERT INTO dbo.[RefStudentSupportServiceType]([Code], [Description], [Definition], [SortOrder])
VALUES ('00302', 'School counseling', 'Programs that provide advice and guidance for students who need assistance in choosing course or areas for major study, preparing for a vocation or further education, or coping with personal or learning problems.', 144.00)
INSERT INTO dbo.[RefStudentSupportServiceType]([Code], [Description], [Definition], [SortOrder])
VALUES ('00320', 'School health nursing services', 'Services provided by a qualified school health nurse or other registered nurse/nurse practitioner.', 148.00)
INSERT INTO dbo.[RefStudentSupportServiceType]([Code], [Description], [Definition], [SortOrder])
VALUES ('00294', 'Service coordination (case management services)', 'Activities carried out by service coordinator to assist and enable an eligible student and his/her family to receive the rights, procedural safeguards, and services that are authorized to provided under the State''s early intervention program. The specific activities include: coordinating the performance of evaluations and assessments; facilitating and anticipating in the development, review, and evaluation of individualized family service plans; assisting families in identifying available service providers; coordinating and monitoring the delivery of available services; informing families of the availability of advocacy services; coordinating with medical and health providers; and facilitating the development of a transition plan to preschool services, if appropriate.', 152.00)
INSERT INTO dbo.[RefStudentSupportServiceType]([Code], [Description], [Definition], [SortOrder])
VALUES ('00337', 'Social work services', 'Services that involve making home visits to evaluate a child''s living conditions and patterns of parent-child interaction; preparing a social or emotional developmental assessment of the child within the family context; providing individual and family-group counseling with parents and other family members, and appropriate social skill-building activities with the child and parents; working with those problems in a child''s and family''s living situation (home, community, and any center where early intervention services are provided) that affect the child''s maximum utilization of early intervention services; and identifying, mobilizing, and coordinating community resources and services to enable the child and family to receive maximum benefit from early intervention services.', 156.00)
INSERT INTO dbo.[RefStudentSupportServiceType]([Code], [Description], [Definition], [SortOrder])
VALUES ('00321', 'Special transportation', 'Transportation arrangements provided to students to and from home, school, or other service sites.', 160.00)
INSERT INTO dbo.[RefStudentSupportServiceType]([Code], [Description], [Definition], [SortOrder])
VALUES ('00322', 'Speech-language therapy', 'Individual or group therapy sessions that focus on the remediation of specific communicative or pharyngeal disorders and delays in the development of communication skills including: 1) speech sounds that are omitted, replaced by substitute sounds, or distorted; 2) voice problems in which pitch, loudness or quality of voice are affected; or 3) stuttering. Therapy includes the identification of an individual with such disorders and developmental delays, including the diagnosis and appraisal of specific disorders and delays in those skills. It also includes referral for medical or other professional services necessary for the habilitation or rehabilitation of individuals with those disorders and developmental delays. Finally, it includes the provision of services for habilitation, rehabilitation, or prevention of such disorder and developmental delays.', 164.00)
INSERT INTO dbo.[RefStudentSupportServiceType]([Code], [Description], [Definition], [SortOrder])
VALUES ('00323', 'Study skills assistance', 'Programs that provide individual or small group assistance for students who want to improve their study habits, test preparation skills and test-taking techniques.', 168.00)
INSERT INTO dbo.[RefStudentSupportServiceType]([Code], [Description], [Definition], [SortOrder])
VALUES ('00324', 'Substance abuse education/prevention', 'Programs that provide a variety of services including: 1) individual, group and/or family counseling; 2) knowledge and skill-enhancing activities; and 3) structured groups that focus on family dynamics, problem solving, self-esteem, and/or similar issues. These programs care for students who are at risk of drug abuse (e.g., those who are currently in an experimental stage (light use), who have family members or peer groups who use alcohol or drugs, or who have behavioral, emotional, or other problems at home, in school, in the community, or with the criminal justice system). Substance abuse education programs also offer presentations about the risks of substance use to students at school and various other community settings.', 172.00)
INSERT INTO dbo.[RefStudentSupportServiceType]([Code], [Description], [Definition], [SortOrder])
VALUES ('00886', 'Supported employment services', 'Programs that find paid, meaningful work in a variety of integrated settings for individuals who have special needs, including those with developmental disabilities. Services include assigning a "job coach" to work side-by-side with each client, offering advocacy to the employer and other employees, training in basic job skills and work-related behaviors, assisting with specific tasks as needed, and providing initial and ongoing support as required to ensure that the individual retains competitive employment.', 176.00)
INSERT INTO dbo.[RefStudentSupportServiceType]([Code], [Description], [Definition], [SortOrder])
VALUES ('00887', 'Technological aids', 'Structural or sensory aids provided to students with special physical or physiological needs in order to help them to better adapt to everyday work and/or learning environments.', 180.00)
